import { SendEmailCommand } from "@aws-sdk/client-ses";
import { sesClient } from "../lib/sesClient.js";

const createSendEmailCommand = (fromAddress, toAddress) => {
  return new SendEmailCommand({
    Destination: {
      /* required */
      CcAddresses: [
        /* more items */
      ],
      ToAddresses: [
        process.env.NEXT_PUBLIC_RECIPIENT_EMAIL
        /* more To-email addresses */
      ],
    },
    Message: {
      /* required */
      Body: {
        /* required */
        Html: {
          Charset: "UTF-8",
          Data: `
            <!DOCTYPE html>
            <html>
              <body style="margin:0; padding:0; font-family: Arial, sans-serif;">
                <div style="max-width: 600px; margin: 0 auto; padding: 20px; width: 100%; background-color: #ffffff;">
                  
                  <div style="display: flex; justify-content: center; margin-bottom: 2rem;">
                    <img src="data:image/png;base64," width="200" />
                  </div>

                  <p style="font-size: 16px;">Hey Denis,</p>

                  <p style="font-size: 16px;">Check out our latest article on <strong>AWS Architecture Pattern for Scheduled & Serverless Batch Processing</strong></p>

                  <p style="font-size: 16px; margin: 3rem 0;">Scheduled batch jobs are the heart of many business processes implemented by enterprise applications. Reports are generated daily, databases are optimized over the weekend, and business logic is executed nightly. The importance of batch jobs satisfies an investment into a reliable architecture to execute the jobs.</p>

                  <a href="http://localhost:3000/aws-architecture-pattern-for-scheduled--serverless-batch-processing" 
                    style="display: inline-block; padding: 10px 20px; background-color: rgb(45, 110, 164); color: white; font-size: 14px; text-decoration: none; border-radius: 14px;">
                    Read Now
                  </a>

                </div>
              </body>
            </html>
          `,
        },
      },
      Subject: {
        Charset: "UTF-8",
        Data: "AWS Architecture Pattern for Scheduled & Serverless Batch Processing",
      },
    },
    Source: process.env.NEXT_PUBLIC_SENDER_EMAIL,
    ReplyToAddresses: [
      /* more items */
    ],
  });
};

export const run = async () => {
  const sendEmailCommand = createSendEmailCommand(
    process.env.NEXT_PUBLIC_RECIPIENT_EMAIL,
    process.env.NEXT_PUBLIC_SENDER_EMAIL
  );

  try {
    return await sesClient.send(sendEmailCommand);
  } catch (caught) {
    if (caught instanceof Error && caught.name === "MessageRejected") {
      /** @type { import('@aws-sdk/client-ses').MessageRejected} */
      const messageRejectedError = caught;
      return messageRejectedError;
    }
    throw caught;
  }
};